import React from 'react';
import '../Cards/Cards.css';
import { useNavigate } from 'react-router-dom';

function Cards({ color, imageSrc, index, totalCards }) {
    const radius = 200; // Adjust this radius to control the curvature of the semi-circle
    const centerX = 400; // Adjust this value to control the horizontal center of the semi-circle
    const centerY = 150; // Adjust this value to control the vertical position of the semi-circle

    const angleStep = Math.PI / (totalCards - 1);
    const angle = index * angleStep - Math.PI / 2;

    const position = {
        top: centerY + radius * Math.sin(angle),
        left: centerX + radius * Math.cos(angle)
    };

    const navigate = useNavigate();

    return (
        <div style={{ ...position}} className="relative">
            <div onClick={() => {navigate('/chuckles', { state: { param1: {index}}})}} className="card w-32 h-48 rounded-md overflow-hidden shadow-lg absolute top-0 left-0">
                <img src={imageSrc} alt={`Clickable placeholder image with background color ${color}, text 'URS' in bold and font size 30.`} />
            </div>
        </div>
    );
}

export default Cards;
